import React, { useState } from 'react';

const ProjectCard = ({ project }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="relative overflow-hidden rounded-xl shadow-2xl transition-all duration-500 hover:shadow-3xl"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="relative h-64 overflow-hidden">
        <img 
          src={project.imageUrl} 
          alt={project.title}
          className={`w-full h-full object-cover transition-transform duration-700 ${isHovered ? 'scale-110' : 'scale-100'}`}
        />
        <div className={`absolute inset-0 bg-gradient-to-t from-black/80 via-transparent to-transparent transition-opacity duration-500 ${isHovered ? 'opacity-100' : 'opacity-80'}`}></div>
      </div>

      <div className="absolute bottom-0 left-0 right-0 p-6 text-white">
        <span className="inline-block px-3 py-1 text-xs font-semibold bg-blue-500 rounded-full mb-2">
          {project.category}
        </span>
        <h3 className="text-xl font-bold mb-1">{project.title}</h3>
        <p className="text-sm opacity-90">{project.description}</p>
      </div>

      <div className={`absolute inset-0 flex items-center justify-center bg-black/70 transition-opacity duration-500 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <button className="px-6 py-2 bg-white text-black rounded-full font-medium transform transition-all hover:scale-105">
          Ver Detalles
        </button>
      </div>
    </div>
  );
};

export default ProjectCard;
