import React, { useState, useEffect } from 'react';

const PortfolioHeader = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`fixed w-full z-50 transition-all duration-500 ${scrolled ? 'bg-white/95 shadow-md backdrop-blur-sm py-3' : 'bg-white/80 py-4'}`}>
      <div className="container mx-auto px-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-gray-900 transition-all duration-300 hover:text-blue-600">
            <span className="text-blue-600">Portafolio</span> Pro
          </h1>
          <nav className="hidden md:flex space-x-8">
            <a href="#projects" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Proyectos</a>
            <a href="#skills" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Habilidades</a>
            <a href="#contact" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Contacto</a>
          </nav>
          <button className="md:hidden focus:outline-none">
            <svg className="w-6 h-6 text-gray-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>
    </header>
  );
};

export default PortfolioHeader;
