import React, { useRef, useEffect } from 'react';

const ProjectCard = ({ project, isHovered }) => {
  const imageContainerRef = useRef(null);

  useEffect(() => {
    const container = imageContainerRef.current;
    if (container && container.scrollHeight > container.clientHeight) {
      let scrollPosition = 0;
      const scrollSpeed = 1; // Ajusta la velocidad del scroll (píxeles por intervalo)
      const scrollInterval = 50; // Ajusta el intervalo de tiempo entre cada desplazamiento (milisegundos)

      const scrollDown = () => {
        if (container.scrollTop < container.scrollHeight - container.clientHeight) {
          scrollPosition += scrollSpeed;
          container.scrollTop = scrollPosition;
        } else {
          // Si llegamos al final, volvemos al inicio después de un breve delay
          setTimeout(() => {
            scrollPosition = 0;
            container.scrollTop = 0;
          }, 1500); // Ajusta el tiempo de espera al final
        }
      };

      const intervalId = setInterval(scrollDown, scrollInterval);

      // Limpiar el intervalo cuando el componente se desmonte o cuando isHovered cambie
      return () => clearInterval(intervalId);
    }
  }, [project.image, isHovered]); // Dependencias para que el efecto se reactive si la imagen cambia

  return (
    <div className={`relative group overflow-hidden rounded-2xl shadow-xl transition-all duration-500 transform ${isHovered ? '-translate-y-2' : ''}`}>
      <div className="relative overflow-hidden overflow-y-auto h-64" ref={imageContainerRef}> {/* Añadimos la referencia */}
        <img
          src={project.image}
          alt={project.title}
          className={`w-full h-auto transition-transform duration-700 ${isHovered ? 'scale-110' : 'scale-105'}`}
          loading="lazy"
          style={{ maxHeight: 'inherit' }}
        />
        <div className={`absolute inset-0 bg-gradient-to-t from-black/80 via-transparent to-transparent ${isHovered ? 'opacity-100' : 'opacity-0'} transition-opacity duration-500 flex items-end p-6`}>
          <div className={`transform ${isHovered ? 'translate-y-0' : 'translate-y-4'} transition-transform duration-500`}>
            <h3 className="text-white text-2xl font-bold">{project.title}</h3>
            <p className="text-gray-200 mt-2">{project.description}</p>
          </div>
        </div>
      </div>
      {/* ... el resto del componente ... */}
    </div>
  );
};

export default ProjectCard;
