import React, { useState, useEffect } from 'react';

const PortfolioHeader = () => {
  const [scrolled, setScrolled] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <header
      className={`fixed w-full z-50 transition-all duration-500 ${
        scrolled ? 'bg-white/95 shadow-md backdrop-blur-sm py-3' : 'bg-white/80 py-4'
      } ${isOpen ? 'mb-[...] md:mb-0' : ''}`} // Añadimos margen inferior condicional
    >
      <div className="container mx-auto px-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-gray-900 transition-all duration-300 hover:text-blue-600">
            <span className="text-blue-600">Portafolio</span> Pro
          </h1>
          <nav className="hidden md:flex space-x-8 items-center">
            <a href="#projects" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Proyectos</a>
            <a href="#skills" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Habilidades</a>
            <a href="#contact" className="text-gray-700 hover:text-blue-600 transition-colors font-medium">Contacto</a>
            <a
              href="/cv"
              target="_blank"
              rel="noopener noreferrer"
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full transition-colors"
            >
              Ver Currículum
            </a>
          </nav>
          <div className="md:hidden">
            <button onClick={toggleMenu} className="focus:outline-none">
              <svg className="w-6 h-6 text-gray-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={isOpen ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"} />
              </svg>
            </button>
          </div>
        </div>

        {isOpen && (
          <div className="md:hidden mt-4 space-y-3 bg-white/95 shadow-md rounded-md py-4 px-6">
            <a href="#projects" className="block text-gray-700 hover:text-blue-600 transition-colors font-medium">Proyectos</a>
            <a href="#skills" className="block text-gray-700 hover:text-blue-600 transition-colors font-medium">Habilidades</a>
            <a href="#contact" className="block text-gray-700 hover:text-blue-600 transition-colors font-medium">Contacto</a>
            <a
              href="/cv"
              target="_blank"
              rel="noopener noreferrer"
              className="block bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full transition-colors text-center"
            >
              Ver Currículum
            </a>
          </div>
        )}
      </div>
    </header>
  );
};

export default PortfolioHeader;
